import { useState } from "react";
import { BarChart, Bar, XAxis, YAxis, Tooltip, Legend, CartesianGrid, ResponsiveContainer } from "recharts";

const ChartComponent = ({ top5Data }) => {
    const [sortedData, setSortedData] = useState(top5Data);
    const [isAscending, setIsAscending] = useState(false);

    const sortData = () => {
        const sorted = [...sortedData].sort((a, b) =>
            isAscending
                ? a["Box Office Revenue"] - b["Box Office Revenue"]
                : b["Box Office Revenue"] - a["Box Office Revenue"]
        );
        setSortedData(sorted);
        setIsAscending(!isAscending);
    };

    return (
        <div style={{ textAlign: "center", padding: "20px" }}>
            <button
                onClick={sortData}
                style={{ marginBottom: "10px", padding: "10px 20px", cursor: "pointer" }}
            >
                {isAscending ? "Сортировать по убыванию" : "Сортировать по возрастанию"}
            </button>

            <div style={{ display: "flex", justifyContent: "center", alignItems: "center", height: "80vh" }}>
                <ResponsiveContainer width="90%" height={600}>
                    <BarChart data={sortedData}>
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis
                            dataKey="Film Title"
                            type="category"
                            tick={{ fontSize: 12 }}
                            interval={0}
                        />
                        <YAxis
                            type="number"
                            tickFormatter={(value) => `$${new Intl.NumberFormat("de-DE").format(value)}`}
                        />
                        <Tooltip
                            formatter={(value) => `$${new Intl.NumberFormat("de-DE").format(value)}`}
                        />
                        <Legend />
                        <Bar dataKey="Box Office Revenue" fill="#4c99d8" animationDuration={2000} />
                    </BarChart>
                </ResponsiveContainer>
            </div>
        </div>
    );
};

export default ChartComponent;
